-- Table structure for data_plantime
-- ----------------------------
DROP TABLE IF EXISTS `data_plantime`;
CREATE TABLE `data_plantime` (
  `PLANID` varchar(36) NOT NULL COMMENT '计划时间ID',
  `PLANTYPE` varchar(32) NOT NULL COMMENT '计划类型：数据字典【计划类型】编码，含义：1-数据抽取计划；2-自动备份计划；3-数据预处理计划；',
  `PLANNAME` varchar(32) NOT NULL COMMENT '计划名称，当“计划类型”为2-自动备份计划，或者3-数据预处理计划时，计划名称默认为计划类型名称；',
  `CYCLE` varchar(32) NOT NULL COMMENT '时间周期：数据字典【时间周期】编码，含义：1-每天；2-每周；3-每月；4-每年某月第一天；5-每年某月最后一天；6-立即执行；7-不执行；',
  `DATE` varchar(32) default NULL COMMENT '日期：数据字典编码，当时间周期为“2-每周”时，日期对应字典【周】含义为：1-周一~7-周日；当时间周期为“3-每月”时，日期对应字典【天】含义为：1-第1天~29-最后一天；当时间周期为“4-每年某月第一天”时，日期对应字典【月】含义为：1-1月~12-12月；当时间周期为“5-每年某月最后一天”时，日期对应字典【月】含义为：1-1月~12-12月；',
  `TIME` varchar(32) default NULL COMMENT '时间',
  `CRONEXP` varchar(32) default NULL COMMENT '时间表达式',
  `CREATETIME` timestamp NOT NULL default CURRENT_TIMESTAMP COMMENT '创建时间，默认系统时间自动生成',
  PRIMARY KEY  (`PLANID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='计划时间表';

-- ----------------------------
-- Records of data_plantime
-- ----------------------------
