INSERT INTO `cms_front_article_history` VALUES ('09859800-4970-4bf3-a5ba-d81abb194b24', '142013', '', '3852354c-c8d7-4e7b-b021-67d6369dceee', '关于下达2014年度省办网站信', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 10:26:31', null, null, '2014-05-07 10:34:05', null, null, '2014-05-07 10:34:23', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '通知公告', '各设区市、各县（市、区）扶贫和移民部门：认真总结2013年度省办网站在省政府组成部门、直属机构和有关单位网站绩效评估无行政审批项目单位网站中排名第一的成功经验，其中之一就是加强网站信息日常更新。为了继续做好2014年底省办网站建设，特别是信息发布工作，现将具体任务分值下达到各设区市、县(市、区)扶贫和移民部门。附件1：2014年度各设区市扶贫和移民部门信息发布任务指导性计划附件2：2014年度各县（市、区）扶贫和移民部门信息发布任务指导性计划省扶贫和移民办政策法规处2014年3月6日');
