INSERT INTO `cms_front_article_history` VALUES ('22401889-97e4-4eaa-8da9-acb048a7175f', '1215', '', '4656effa-8292-49d8-8bec-c9c19bee2cbb', '后期扶持', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 16:14:37', null, null, '2014-05-07 16:15:16', null, null, '2014-05-07 16:15:39', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '后期扶持', '4月30日，南城县扶贫和移民局及时组织3名专业技术人员深入该县西瓜种植专业村——洪门镇沙坪村进行西瓜技术培训班，对全村瓜农进行系统培训和技术指导，实现科技入户与农民“零距离”。培训班上，技术人员用通俗易懂的语言，对西瓜的种植产知识进行深入细致的讲解。听了专家讲解，广大种植户对瓜田的选择、整地施肥、座果期管理、防治病虫害等方面均有了更加全面的了解，增强了科技种植西瓜的信心。离别时，该县扶贫和移民局工作人员还为西瓜种植户送去30多袋西瓜种植专用复合肥料。针对农村科技人才缺乏、科技难应用、科技成果转化率低的特点，在开展党的群众路线教育实践活动中，南城县扶贫和移民局在深入一线听取群众意见的基础上，大力实行科技入户工程，精心织就“科技网络”，变“送科技”为“种科技”，与农民“零距离”，破解科技入户“最后一公里”难题。该局建立起一支由专家——指导员——示范户——协会为骨干的科技推广队伍，采取定期聘请相关专家上门“坐诊”，让农民自己“谈病情”，再对症下药的方法提升技术传播的有效率。同时聘请当地部分种田、种菜、养鱼能手当“老师”，由他们负责组建科普推广栏，针对每一时期生产中比较集中的问题向农民发布农用物质供应、市场运销情况、天气影响、病虫害防治等信息；并让他们利用闲暇时光免费进行农技推广、培训，在推广中注重说干结合，田间指导，把理论与实践操作有机结合起来。到目前为止，该县扶贫和移民局共为农民发放种养书籍1200册，举办种养科技培训班6期，建立科技示范点3个，发布“农事信息”1600多条，解决实际种养难题300多个，以实际行动践行了党的群众路线教育。');
INSERT INTO `cms_front_article_history` VALUES ('228f482667eb4c3fbcc51cd40f2fc756', '142013', '', 'c8d41296-2c2d-405f-ab38-ffbebb7145b2', '移民办网站信息采用情况的通报', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 11:47:43', null, null, null, null, null, null, '0', null, null, null, '', '', '', '0', null, null, null, null, null, null, '通知公告', '各设区市、各县(市、区)扶贫和移民部门:自2012年底省扶贫和移民办网站改版并由政策法规处全面负责日常信息更新以来，省办网站呈现出良好的发展态势，特别是3月15日全省扶贫和移民系统主要负责人暨党风廉政建设工作会议上，饶振华副主任就网站信息化建设进行强调后，大部分市、县扶贫和移民部门积极投稿，为省办网站提供了及时的信息。但也有部分市、县扶贫和移民部门不重视网站宣传，至今没有一条信息发布。截止4月12日中午12时，12个设区市扶贫和移民部门中，仅有南昌市移民办没有发布一条信息。信息发布得分10分以上的有:新余市扶贫和移民办17分、吉安市扶贫和移民办11分、九江市扶贫和移民办11分;114个县(市、区)扶贫和移民部门中，57个县有信息发布，另外57个县没有发布一条信息。在57个有信息发布的县中，得分10分以上的有:安远县、信丰县、铜鼓县、奉新县、乐安县、上犹县、樟树市、全南县、龙南县、定南县、峡江县、吉安县、鄱阳县、井冈山市、余江县、余干县。希望受表扬的市、县扶贫和移民部门再接再厉，受批评的市、县扶贫和移民部门要高度重视。未在省办网站发布一条信息的市、县扶贫和移民部门，于本月底前将整改方案报省办政策法规处。省办政策法规处将每季度后10天内通报一次。                     省扶贫和移民办政策法规处2013年4月12日');
