INSERT INTO `cms_front_article_history` VALUES ('37a8a748-fee7-440c-af54-257e32c982f6', '141112', '', 'f29b2242-a472-42af-a688-18af87150680', '江西扶贫和移民工作简报2013年第11期', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:15:48', null, null, '2014-05-07 13:17:32', null, null, null, '2', null, null, null, '', '', '', '1', null, null, null, null, null, null, '工作简报', '为协调推进《罗霄山片区区域发展与扶贫攻坚规划》实施，2月24日，罗霄山片区区域发展与扶贫攻坚工作会议在湖南省郴州市的汝城县召开，这是继2013年2月在江西赣州召开首次片区工作会议以来的第二次会议。会议由民政部部长李立国主持，湖南省委副书记、省长杜家毫，国务院扶贫办主任刘永富，民政部副部长宫蒲光，湖南省副省长张硕辅，江西省副省长姚木根等出席会议并讲话。');
