INSERT INTO `cms_front_article_history` VALUES ('4ff1f602-36cb-4b00-9ec2-ff2c4a61db52', '141112', '', 'bc491ad7-7590-496e-b553-f95091e283c4', '江西扶贫和移民工作简报2014年第4期', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:14:55', null, null, '2014-05-07 13:17:36', null, null, '2014-05-07 13:18:06', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '工作简报', '编者案：搬迁移民扶贫进城进园是一个系统工程，需要统筹各方力量，最大限度的整合各项政策，才能实施好。武宁县在工作中积极探索，以两办名义出台了《武宁县移民扶贫搬迁政策项目资金整合暂行办法》，从组合惠农政策、整合项目资金、综合部门职能和融合扶持方式等四个方面，对19个部门和乡镇的职责和资金进行整合，取得了很好的效果，具有指导意义，值得学习借鉴。现专刊印发，供交流参考。');
