INSERT INTO `cms_front_article_history` VALUES ('a15a6c0015aa4edabf574cac8efef109', '141314', '', 'e4861eaf-3366-4adc-82b1-e7d740bb0461', '贫困村人民实现安居梦', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 15:42:16', null, null, null, null, null, null, '0', null, null, null, '', '', '', '0', null, null, null, null, null, null, '整村推进', '走进国家级贫困村---宁都县小布镇大土楼村，一排排小洋楼拔地而起，一条条水泥路四通八达，一盏盏路灯新颖别致，一股股自来水流进农家，粉墙黛瓦，花红草绿，整齐划一的新居点缀在如画的风景里。“人民政府就是好，农村危房全改造,为民解困建新房，党的恩情比天高……”侧耳细听，前方飘来阵阵山歌声。循声而去，一栋小洋楼前，地上洒满了鞭炮纸屑和五颜六色的彩带。村民吴传寿家正在宴请亲戚朋友，喜圆新居梦难捺喜悦之情，当着大伙演唱了自己创作的山歌《老区人民喜圆安居梦》。大土楼村开门就见山，利用这“非同一般”的地形，曾成功击退了国民党的“围剿”。还是这“非同一般”的地形，让村民们祖祖辈辈居住在破陋的土坯房，过着致富难、住房难的困苦生活。“过去每逢雨天，心里直打颤，晚上睡都不安稳，怕屋倒砸伤人。”谈起以前的居住环境，吴传寿神色略显不安。过去，他一家5口挤在一套不到50平方米的危旧土坯房里，砖墙瓦顶，年久失修。没有卧室，就用木板隔成一小间一小间，下雨屋里漏水就用盆来接。');
INSERT INTO `cms_front_article_history` VALUES ('a1a00540-13a7-4ce6-96ba-8f4fb4b896a3', '141112', '', 'f13ec036-475e-4b68-a651-a252b51f0bfd', '江西扶贫和移民工作简报2014年第6期', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:14:03', null, null, '2014-05-07 13:17:41', null, null, null, '2', null, null, null, '', '', '', '1', null, null, null, null, null, null, '工作简报', '省委书记强卫到修水县调研整体移民搬迁工作省委副书记尚勇到龙南县考察扶贫移民搬迁等工作省委副书记尚勇：深入推进扶贫开发工作章康华主任到铜鼓县调研扶贫和移民工作湖南省调研组来赣考察调研社会扶贫工作省“两办”出台创新扶贫机制改革工作实施方案《农民日报》采访团对峡江水利枢纽工程移民安置工作进行采风 九江市新增专项资金300万推进精准扶贫赣州市全面完成扶贫对象识别工作吉州区精准扶贫助民富萍乡市“六到位”确保扶贫移民项目监督管理');
