INSERT INTO `cms_front_article_history` VALUES ('c4b9f43b-4575-4960-8101-ef257ce74de3', '142013', '', 'dd6e7cf5-3c0b-47cf-944f-96a9385bc188', '关于上报定点扶贫工作情况的通知', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 11:49:14', null, null, '2014-05-07 11:50:02', null, null, '2014-05-07 11:50:26', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '通知公告', '市、县(区、市)扶贫和移民办(局)：根据省委、省政府“全省扶贫开发工作推进会”要求，为进一步做好全省定点包扶、定点扶贫基础性工作，决定对全省3400个扶贫开发重点村的基本情况及新一轮定点包扶、定点扶贫单位的帮扶工作情况进行一次调查。现将有关事项通知如下：');
INSERT INTO `cms_front_article_history` VALUES ('c54243fa-e275-4edf-a721-b3b18de1e909', '142013', '', '0ee653be-0f21-4235-a65f-46c6247d0ebd', '2014年度扶贫和移民调研工作方案', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 11:45:37', null, null, '2014-05-07 11:50:20', null, null, null, '2', null, null, null, '', '', '', '1', null, null, null, null, null, null, '通知公告', '为深入贯彻党的十八届三中全会精神和《关于创新机制扎实推进农村扶贫开发工作的意见》文件精神，推动我省扶贫和移民工作改革创新，促进各地各部门进一步重视调查研究工作，密切与群众联系，发现问题、总结经验、推广典型，不断提高调研水平，实现决策的科学化、民主化，特制定本方案。一、2014年度调研课题1、精准扶贫方式方法研究2、干部驻村帮扶机制的建立与完善3、如何创新扶贫开发工作的社会参与4、扶贫和移民部门在推进行业扶贫中的地位和作用5、移民社区社会管理的探索与创新6、如何加强扶贫和移民工作绩效管理7、扶贫和移民工作的效率与公平问题8、水库移民安置与后扶工作创新研究各地各部门可结合实际，创新调研思路和工作方法，在上述8大课题内，自主选择更为细化的具体调研课题。二、调研文章数量年度指导性计划赣州市12篇，吉安市10篇，上饶市9篇，抚州市8篇，萍乡市、九江市各5篇，宜春市4篇，鹰潭、景德镇、新余、南昌市各3篇，省办各业务处室2篇以上。多者不限。今年将加强调研文章的进度管理，请将年度任务合理分解并及时报送到政策法规处或网站（注明：调研文章），每季度将反馈各地各部门提交调研文章数量。');
