INSERT INTO `cms_front_article_history` VALUES ('de7479a9-a961-4d64-be73-c25257262050', '141112', '', '0202fee6-a089-41bd-a635-64848e9831e8', '江西扶贫和移民工作简报2014年第5期', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:14:25', null, null, '2014-05-07 13:17:38', null, null, '2014-05-07 13:18:08', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '工作简报', '编者案：修水县以两办名义下发了《关于印发〈推进整体移民搬迁加快城乡发展一体化2014年工作要点〉的通知》，对2014年的搬迁移民扶贫进城进园集中安置工作，从目标、任务、要求和保障措施等方面进行规范，并提出了明确的责任单位和完成时间要求。组织严密、措施有力、工作扎实。现专刊印发，供各地学习和借鉴。推进整体移民搬迁加快城乡发展一体化2014年工作要点');
