INSERT INTO `cms_front_article_history` VALUES ('e29dc394-7856-4487-b92c-5e1a589ee035', '141112', '', 'f13ec036-475e-4b68-a651-a252b51f0bfd', '江西扶贫和移民工作简报2014年第6期', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:14:03', null, null, '2014-05-07 13:17:41', null, null, '2014-05-07 13:18:11', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '工作简报', '省委书记强卫到修水县调研整体移民搬迁工作省委副书记尚勇到龙南县考察扶贫移民搬迁等工作省委副书记尚勇：深入推进扶贫开发工作章康华主任到铜鼓县调研扶贫和移民工作湖南省调研组来赣考察调研社会扶贫工作省“两办”出台创新扶贫机制改革工作实施方案《农民日报》采访团对峡江水利枢纽工程移民安置工作进行采风 九江市新增专项资金300万推进精准扶贫赣州市全面完成扶贫对象识别工作吉州区精准扶贫助民富萍乡市“六到位”确保扶贫移民项目监督管理');
