INSERT INTO `cms_front_article_history` VALUES ('e8d7d0f8-1347-4f26-8285-5910b98891b7', '141112', '', 'c9fb731e-20aa-43d0-8a90-17a2f5baecb0', '江西扶贫和移民工作简报2013年第8期', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:16:49', null, null, '2014-05-07 13:17:28', null, null, '2014-05-07 13:17:56', '3', null, null, null, '', '', '', '2', null, null, null, null, null, null, '工作简报', '工作调研省长鹿心社调研峡江水利枢纽工程建设和移民安置工作省委副书记尚勇调研共青城市移民搬迁扶贫工作章康华主任在乐安县调研扶贫开发工作章康华主任调研浯溪口水利枢纽移民安置工作会议动态全省水库移民后期扶持工作绩效考评汇报会召开市县交流');
INSERT INTO `cms_front_article_history` VALUES ('e8f1bc15b88940cea87569442ae2e125', '1218', '', '11b81566-6c8d-40ad-aa2d-7168f73781c0', '上饶市对未到帮扶点开展定', null, '', '05e3d3c7-2e43-42b8-97df-af5498dc38d5', '江西省', '0', '2014-05-07 13:24:22', null, null, null, null, null, null, '0', null, null, null, '', '', '', '0', null, null, null, null, null, null, '监测稽查', ' 近日，上饶市扶贫和移民办联合上饶市委组织部、上饶军分区政治部对第二轮市直定点包扶单位进行了全面检查，检查中发现10家定点包扶市直单位未到点开展工作。针对检查情况，结合贫困村的反映和贫困村村民的愿望，上饶市扶贫和移民办依据扶贫工作需要和群众诉求，将情况反映到上饶市委主要领导，由上饶市委组织组向至今未到点开展工作的10家单位进行一对一的约谈。这10家单位主要领导纷纷表示将严格按照市委、市政府的要求和部署，今后将一以贯之地做好定点包扶贫困村工作，并尽快到扶贫点进行对口帮扶、开展点对点的工作。');
